import{_ as a,D as s,o,c as r,I as n,k as e,a as c}from"./chunks/framework.VHr1O9iJ.js";const N=JSON.parse('{"title":"房租分摊算法","description":"如果两个人合租房子，如何合理的分摊房租？","frontmatter":{"date":"2023-12-23T00:00:00.000Z","title":"房租分摊算法","tags":["算法"],"description":"如果两个人合租房子，如何合理的分摊房租？"},"headers":[],"relativePath":"posts/rent-share.md","filePath":"posts/rent-share.md"}'),l={name:"posts/rent-share.md"},d=e("h1",{id:"房租分摊算法",tabindex:"-1"},[c("房租分摊算法 "),e("a",{class:"header-anchor",href:"#房租分摊算法","aria-label":'Permalink to "房租分摊算法"'},"​")],-1),i=e("p",null,"两个人A，B合租一个二居的房子，比如每个月是1500美元，因为主卧和次卧有大有小，价格肯定是不均的，那么两个人分别写两个价格，也就是对主卧和次卧的心理价格。可以很极端，比如1400：100，但总额必须是1500，因为这是A，B必须接受的大条件，然后公开，除掉开价完全相当的情况，两间卧室必然各有一个出价最高的人，价高者入住，而月租则是A，B对这个卧室开价的均值。例如A出价是900：600，B出价是1000：500，那么A住次卧，价格为550，B住主卧，价格为950。两个人都得到了自己认可的房子，而价格还低于自己的预期。",-1),_=e("p",null,"这一方案还有一个优势，就是双方都无法通过恶意的叫价来损害对方，获得利益。",-1),p=e("p",null,"这个思想在现实生活中，不少场合能用到。",-1);function h(m,f,u,B,A,k){const t=s("Artalk");return o(),r("div",null,[d,i,_,p,n(t)])}const $=a(l,[["render",h]]);export{N as __pageData,$ as default};
